import theme_ from "theme";

import { APIContext, AppContext } from "../containers/controllers/Contexts";
import * as MiscComponents from "../components/Misc";
import Button_ from "../components/Button";
import Input_ from "../components/Input";


export const TextBlock = MiscComponents.TextBlock;
export const Button = Button_;
export const Input = Input_;

export const theme = theme_;
export const apiContext = APIContext;
export const appContext = AppContext;

export let plugins = {
    categoryType: {},
    challengeType: {},
};

// Export the plugins object for debugging purposes.
window.__plugins = plugins;

export const registerPlugin = (type, key, handler) => {
    if (!plugins[type]) plugins[type] = {};
    plugins[type][key] = handler;
}
