import {
    darken, saturate, lighten, desaturate
} from "polished";


const theme = {
    font_stack: "'Roboto Mono', monospace",
    background: "#222034",
    foregound: "#fff",

    green: "#115423",
}

//theme.background = "#606"

theme.bg = theme.background;
theme.fg = theme.foregound;

theme.bg_l1 = desaturate(.05, lighten(.07, theme.bg));
theme.bg_l2 = desaturate(.09, lighten(.14, theme.bg));
theme.bg_l3 = desaturate(.11, lighten(.28, theme.bg));
theme.bg_d1 = saturate(.04, darken(.08, theme.bg))
theme.bg_d0 = saturate(.04, darken(.04, theme.bg))

export default theme;
